//======================================================================================////																						////							Title Program													////																						////======================================================================================//#include		<windows.h>#include		<stdio.h>#include		<math.h>#include		"MASTER/Ci-Lib.H"#include		"Game.H"//****************************************************************************//	■	定義////****************************************************************************//****************************************************************************//	■	内部使用　変数////****************************************************************************GsTEXTURE	TitleBG;GsTEXTURE	TitleBlackBG;GsTEXTURE	Start;GsSOUND		BGM_1;GsSOUND		SE_1;short	TouchFade, TouchFadeAdd;short	KF,KFAdd;short	KUROF;//--------------------------------------------------------------------------////	●	初期化	：シーンに移る時一回、変数の初期化//--------------------------------------------------------------------------//void	TitleInit(void){	Gs_PlayBGM(BGM_1);	TouchFade = 0;	TouchFadeAdd = 2.5;	KF = 0;	KFAdd = 5;}//--------------------------------------------------------------------------////	●	内部処理：シーン中の処理、現在のシーンで行う処理//--------------------------------------------------------------------------//void	TitleLoop(void){	if (TouchFade >= 0) {		TouchFade = TouchFade + TouchFadeAdd;	}	if (TouchFade >= 255) {		TouchFade = 0;	}	if (Mouse.Trg&_lMOUSE) {		Gs_PlaySE(SE_1);		KUROF = ON;	}	if (KUROF == ON) {		KF = KF + KFAdd;	}	if (KF >= 255) {		KF = 255;		KFAdd = 0;	}	if (KF == 255) {		SceneChange(GAME_SCENE);	}}//--------------------------------------------------------------------------////	●	描画	：シーン中の描画、現在のシーンで表示するBMPはここで//--------------------------------------------------------------------------//void	TitleDraw(void){	Gs_DrawLayer(667, 375, TitleBG, 0, 0, 1334, 750, OFF, ARGB(255, 255, 254, 255), ON, 0, 1.0f, 1.0f);	Gs_DrawLayer(667, 625, Start, 0, 0, 576, 100, OFF, ARGB(TouchFade, 255, 254, 255), ON, 0, 1.0f, 1.0f);	if (KUROF == ON) {		Gs_DrawLayer(667, 375, TitleBlackBG, 0, 0, 1334, 750, OFF, ARGB(KF, 255, 254, 255), ON, 0, 1.0f, 1.0f);	}}//--------------------------------------------------------------------------////	●	ロード	：シーンに入る時、そのシーンで必要なデータの読み込み//--------------------------------------------------------------------------//short	TitleLoad(void){	TitleBG = Gs_LoadBMP("DATA/BMP/タイトル背景.PNG");	TitleBlackBG = Gs_LoadBMP("DATA/BMP/黒背景.PNG");	Start = Gs_LoadBMP("DATA/BMP/スタート.PNG");	BGM_1 = Gs_LoadWAVE("DATA/SOUND/サイバー44.WAV", ON);	SE_1 = Gs_LoadWAVE("DATA/SOUND/タイトルタップ.WAV", OFF);	return		TITLE_SCENE;}//--------------------------------------------------------------------------////	●	後処理	：現在のシーンから抜ける時、データの破棄//--------------------------------------------------------------------------//void	TitleExit(void){	Gs_ReleaseBMP(TitleBG);	Gs_ReleaseBMP(TitleBlackBG);	Gs_ReleaseBMP(Start);	Gs_ReleaseSOUND(BGM_1);	Gs_ReleaseSOUND(SE_1);}//--------------------------------------------------------------------------////	★　シーンテーブル：各シーンで呼び出される関数の登録//--------------------------------------------------------------------------//SCENE_TBL	TitleSceneTbl = {	TitleLoad,				// シーンが切り替わった時、各シーンで使用する絵、音のロード	TitleInit,				// 各シーンで使用する変数初期化	TitleLoop,				// 各シーンのメイン処理	TitleDraw,				// 各シーンの描画	TitleExit					// シーンから抜ける（移動する）時の各シーンの後処理(ＢＭＰなどの破棄）};//======================================================================================////							EOP															////======================================================================================//